windowclass.script:
function onInit()
	onLockChanged();
	DB.addHandler(DB.getPath(getDatabaseNode(), "locked"), "onUpdate", onLockChanged);
end
function onClose()
	DB.removeHandler(DB.getPath(getDatabaseNode(), "locked"), "onUpdate", onLockChanged);
end
function onLockChanged()
	if header.subwindow then
		header.subwindow.update();
	end
	if content.subwindow then
		content.subwindow.update();
	end
end

function onInit()
	update();
end
function update()
	local bReadOnly = WindowManager.getReadOnlyState(getDatabaseNode());
	name.setReadOnly(bReadOnly);
end

function onInit()
	update();
end
function update()
	local bReadOnly = WindowManager.getReadOnlyState(getDatabaseNode());
	description.setReadOnly(bReadOnly);
	clichatcommand.setReadOnly(bReadOnly);

	tracker_label.setVisible(not bReadOnly);
	tracker_enabled.setVisible(not bReadOnly);
	tracker_number.setVisible(not bReadOnly);
	number_trackers.setVisible(not bReadOnly);
	trackers_icon.setVisible(not bReadOnly);

	formula_label.setVisible(not bReadOnly);
	parameter_formula_enabled.setVisible(not bReadOnly);
	formula_label_parameter.setVisible(not bReadOnly);
	parameter_formula.setVisible(not bReadOnly);

	checkboxcolumnsanchor.setVisible(not bReadOnly);
	p1_hidden.setVisible(not bReadOnly);
	p2_hidden.setVisible(not bReadOnly);
	p3_hidden.setVisible(not bReadOnly);
	hide_label.setVisible(not bReadOnly);
	hide_p1_label.setVisible(not bReadOnly);
	hide_p2_label.setVisible(not bReadOnly);
	hide_p3_label.setVisible(not bReadOnly);

	tooltip_p1_label.setVisible(not bReadOnly);
	tooltip_p2_label.setVisible(not bReadOnly);
	tooltip_p3_label.setVisible(not bReadOnly);
	p1_tooltip.setVisible(not bReadOnly);
	p2_tooltip.setVisible(not bReadOnly);
	p3_tooltip.setVisible(not bReadOnly);

	refa_label.setVisible(not bReadOnly);
	refb_label.setVisible(not bReadOnly);
	refc_label.setVisible(not bReadOnly);
	refa.setVisible(not bReadOnly);
	refb.setVisible(not bReadOnly);
	refc.setVisible(not bReadOnly);


	line2.setVisible(not bReadOnly);
	line3.setVisible(not bReadOnly);
	line4.setVisible(not bReadOnly);

	if (clichatcommand.getValue() == "" and bReadOnly) then
		clichatcommand.setVisible(false);
		line1.setVisible(false);
		ref_label.setVisible(false);
	else
		clichatcommand.setVisible(true);
		ref_label.setVisible(true);
	end
end

